import React from 'react'

class Header extends React.Component {

  constructor(props) {
    super(props)
    this.state = { menuOpen: false }

    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(e) {
    this.setState(state => ({
      menuOpen: !state.menuOpen
    }))
    document.body.classList.toggle('lockScroll')
  }

  render() {

    let name = this.props.name
    let hamburgerClass = "hamburger hamburger--collapse"

    return(
      <div>
        <header
          className={ this.state.menuOpen ? 'menuOpen' : null }
          >

          <div className="placeholder"></div>

          <h1
            className={ this.state.menuOpen ? 'venue hidden'  : 'venue' }
            >
              { name }
          </h1>

          <div className="placeholder"></div>

          <div
            className={ this.state.menuOpen ? hamburgerClass + ' is-active' : hamburgerClass }
            onClick={ this.handleClick }>
            <div className="hamburger-box">
              <div className="hamburger-inner"></div>
            </div>
          </div>

        </header>
        {/* Menu testing - To be deleted */}
        { this.state.menuOpen ?
          <div className="linksWrapper">
            <div className="testing"><p>debug:<br/>menu open</p></div>
            <ul className="links">
              <li><img src="/Mr_Yum_logo_white.svg" alt="Mr Yum"/></li>
              <a href="#
                0"
                onClick={ handleClick }
                ><li>All Day Menu</li></a>
              <a href="#
                1"
                onClick={ handleClick }
                ><li>All Afternoon Menu</li></a>
              <a href="#
                2"
                onClick={ handleClick }
                ><li>All Night Menu</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
              <a
                href="/"
                onClick={ handleClick }
              ><li>Placeholder thing</li></a>
            </ul>
          </div>
          :
          <div className="testing"><p>debug:<br/>menu closed</p></div>
        }
      </div>
    )
  }
}

export default Header;
