import React from 'react'
import AnchorLink from 'react-anchor-link-smooth-scroll'

class Header extends React.Component {

  constructor(props) {
    super(props)
    this.state = { menuOpen: false }

    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(e) {
    this.setState(state => ({
      menuOpen: !state.menuOpen
    }))
    document.body.classList.toggle('lockScroll')
  }

  render() {

    let name = this.props.name
    let hamburgerClass = "hamburger hamburger--collapse"
    let sections = this.props.sections

    return(
      <div>
        <header
          className={ this.state.menuOpen ? 'menuOpen' : null }
          >

          <div className="placeholder"></div>

          <h1
            className={ this.state.menuOpen ? 'venue hidden'  : 'venue' }
            >
              { name }
          </h1>

          <div className="placeholder"></div>

          <div
            className={ this.state.menuOpen ? hamburgerClass + ' is-active' : hamburgerClass }
            onClick={ this.handleClick }>
            <div className="hamburger-box">
              <div className="hamburger-inner"></div>
            </div>
          </div>

        </header>
        {/* Menu testing - To be deleted */}
        { this.state.menuOpen ?
          <div className="linksWrapper">
            <ul className="links">
              <li><img src="/Mr_Yum_logo_white.svg" alt="Mr Yum"/></li>
              <AnchorLink
                href="#0"
                onClick={ this.handleClick }
                ><li>Bites</li></AnchorLink>
              <AnchorLink
                href="#1"
                onClick={ this.handleClick }
                ><li>Charcuterie</li></AnchorLink>
              <AnchorLink
                href="#2"
                onClick={ this.handleClick }
                ><li>Small Plates</li></AnchorLink>
              <AnchorLink
                href="#3"
                onClick={ this.handleClick }
              ><li>Main</li></AnchorLink>
              <AnchorLink
                href="#4"
                onClick={ this.handleClick }
              ><li>Sides</li></AnchorLink>
              <AnchorLink
                href="#5"
                onClick={ this.handleClick }
              ><li>Sweet</li></AnchorLink>
              <AnchorLink
                href="#6"
                onClick={ this.handleClick }
              ><li>Cheese</li></AnchorLink>
              <AnchorLink
                href="#7"
                onClick={ this.handleClick }
              ><li>Another Section</li></AnchorLink>
              <AnchorLink
                href="#0"
                onClick={ this.handleClick }
              ><li>Maybe Another</li></AnchorLink>
              <AnchorLink
                href="#0"
                onClick={ this.handleClick }
              ><li>Third From Last</li></AnchorLink>
              <AnchorLink
                href="#0"
                onClick={ this.handleClick }
              ><li>Hi Mom</li></AnchorLink>
              <AnchorLink
                href="#0"
                onClick={ this.handleClick }
              ><li>Mr Yummy</li></AnchorLink>
            </ul>
          </div>
          :
          null
        }
      </div>
    )
  }
}

export default Header;
