import React from 'react'

class Header extends React.Component {

  constructor(props) {
    super(props)
    this.state = { menuOpen: false }

    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(e) {
    this.setState(state => ({
      menuOpen: !state.menuOpen
    }))
  }

  render() {

    let name = this.props.name

    return(
      <div>
        <header
          className={ this.state.menuOpen ? 'menuOpen' : null }
          >
          <input
            className="placeholder"
            id="burger"
            type="checkbox"
          />
          <h1 className="venue">{ name }</h1>
          <label htmlFor="burger"
            onClick={ this.handleClick }
            >
            <span></span>
            <span></span>
            <span></span>
          </label>
        </header>
        { this.state.menuOpen ?
          <nav>
            <ul>
              <li><h2>Mr Yum</h2></li>
              <li><a href="#">Section #1</a></li>
              <li><a href="#">Section #2</a></li>
              <li><a href="#">Secion #3</a></li>
              <li><a href="#">Section #4</a></li>
              <li><a href="#">Section #5</a></li>
              <li><a href="#">Secion #6</a></li>
              <li><a href="#">Section #7</a></li>
              <li><a href="#">Section #8</a></li>
              <li><a href="#">Secion #9</a></li>
            </ul>
          </nav>
          :
          <div className="menu">menu closed</div>
        }
      </div>
    )
  }
}

export default Header;
