import React from 'react'

class Header extends React.Component {

  constructor(props) {
    super(props)
    this.state = { menuOpen: false }

    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(e) {
    this.setState(state => ({
      menuOpen: !state.menuOpen
    }))
  }

  render() {

    let name = this.props.name
    let hamburgerClass = "hamburger hamburger--collapse"

    return(
      <div>
        <header
          className={ this.state.menuOpen ? 'menuOpen' : null }
          >

          <div className="placeholder"></div>

          <h1 className="venue">{ name }</h1>

          <div
            className={ this.state.menuOpen ? hamburgerClass + ' is-active' : hamburgerClass }
            onClick={ this.handleClick }>
            <div className="hamburger-box">
              <div className="hamburger-inner"></div>
            </div>
          </div>

        </header>
        { this.state.menuOpen ?
          <div className="menu">menu open</div>
          :
          <div className="menu">menu closed</div>
        }
      </div>
    )
  }
}

export default Header;
